package co.edu.unbosque.model;

public class BetDTO {

	private int day;
	private int month;
	private int year;

	private double betPlaced;

	private String headQuarterName;
	private long document;

	public BetDTO() {
		// TODO Auto-generated constructor stub
	}

	public BetDTO(int day, int month, int year, double betPlaced, String headQuarterName, long document) {
		super();
		this.day = day;
		this.month = month;
		this.year = year;
		this.betPlaced = betPlaced;
		this.headQuarterName = headQuarterName;
		this.document = document;
	}

	public int getDay() {
		return day;
	}

	public void setDay(int day) {
		this.day = day;
	}

	public int getMonth() {
		return month;
	}

	public void setMonth(int month) {
		this.month = month;
	}

	public int getYear() {
		return year;
	}

	public void setYear(int year) {
		this.year = year;
	}

	public double getBetPlaced() {
		return betPlaced;
	}

	public void setBetPlaced(double betPlaced) {
		this.betPlaced = betPlaced;
	}

	public String getHeadQuarterName() {
		return headQuarterName;
	}

	public void setHeadQuarterName(String headQuarterName) {
		this.headQuarterName = headQuarterName;
	}

	public long getDocument() {
		return document;
	}

	public void setDocument(long document) {
		this.document = document;
	}

	@Override
	public String toString() {
		return "BetDTO [day=" + day + ", month=" + month + ", year=" + year + ", betPlaced=" + betPlaced
				+ ", headQuarterName=" + headQuarterName + ", document=" + document + "]";
	}

}